import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const Header = () => {
  const [navbarClass, setNavbarClass] = useState("");

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setNavbarClass("scrolled awake");
      } else {
        setNavbarClass("");
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Navbar expand="lg" bg="dark" variant="dark" className={`ftco_navbar ftco-navbar-light ${navbarClass}`}>
      <Container>
        <Navbar.Brand href="#">
          Coffee<small>Blend</small>
        </Navbar.Brand>
        <Navbar.Toggle className='navbar-toggler' aria-controls="ftco-nav"><span className='oi oi-menu'></span>Menu</Navbar.Toggle>
        {/* <Navbar.Toggle className='navbar-toggler' aria-controls="ftco-nav" /> */}
        <Navbar.Collapse id="ftco-nav">
          <Nav className="navbar-nav ml-auto">
            <Nav.Link href="#" className="nav-link">Home</Nav.Link>
            <Nav.Link href="#" className="nav-link">Menu</Nav.Link>
            <Nav.Link href="#" className="nav-link">Services</Nav.Link>
            <Nav.Link href="#" className="nav-link">About</Nav.Link>
            <Nav.Link href="#" className="nav-link">Contact</Nav.Link>
            <Nav.Link href="#" className="nav-link">Login</Nav.Link>
            <Nav.Link href="#" className="nav-link">Register</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
