import React, { useState, useEffect } from 'react';

const Header = () => {
    const [navbarClass, setNavbarClass] = useState("");
  
    useEffect(() => {
      const handleScroll = () => {
        if (window.scrollY > 50) {
          setNavbarClass("scrolled awake");
        } else {
          setNavbarClass("");
        }
      };
  
      window.addEventListener("scroll", handleScroll);
  
      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }, []);



const Header = () => {
  return (
    <div>
        <div className={`navbar navbar-expand-lg navbar-dark ftco_navbar bg-dark ftco-navbar-light ${navbarClass}`} id="ftco-navbar">
            <div className="container">
                <a className="navbar-brand" href="#">
                    Coffee<small>Blend</small>
                </a>
                <button className='navbar-toggler' type='button' data-toggle='collapse' data-target='#ftco-nav' aria-expanded='false' aria-label='Toggle navigation'>
                    <span className='oi oi-menu'></span> Menu
                </button>
            </div>
        </div>
    </div>
  )
};
};
export default Header;
