import React, { useEffect } from "react";
import Slider from "react-slick";
import { Scrollax } from "scrollax";
import image1 from "../assets/images/bg_1.jpg";
import image2 from "../assets/images/bg_2.jpg";
import image3 from "../assets/images/bg_3.jpg";

const Main = () => {
    useEffect(() => {
        if (Scrollax) {
          Scrollax.init();
        }
        return () => {
          if (Scrollax) Scrollax.destroy(); // Cleanup on unmount
        };
      }, []);
    
      const settings = {
        arrows: false,
        dots: true,
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 2000,
        autoplaySpeed: 2000,
        cssEase: "linear"
      };
  return (
      <Slider className='home-slider' {...settings}>
        <div style={{backgroundImage: `url(${image1})`}}>
            
        </div>
        <div style={{backgroundImage: `url(${image2})`}}>
            
        </div>
        <div style={{backgroundImage: `url(${image3})`}}>
            
        </div>
      </Slider>
  )
}

export default Main
