import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';

const Header = () => {
  const [navbarClass, setNavbarClass] = useState("");

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setNavbarClass("scrolled awake");
      } else {
        setNavbarClass("");
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Navbar expand="lg" bg="dark" variant="dark" className={`ftco_navbar ftco-navbar-light ${navbarClass}`}>
      <Container className='custom-container'>
        <Navbar.Brand as={NavLink} to="/">
          Coffee<small>Blend</small>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="ftco-nav"><span className='oi oi-menu'></span>Menu</Navbar.Toggle>
        <Navbar.Collapse id="ftco-nav">
          <Nav className="ml-auto">
            <Nav.Item>
                <Nav.Link as={NavLink} to="/" className="nav-item">Home</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#" className="nav-item">Menu</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#" className="nav-item">Services</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#" className="nav-item">About</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#" className="nav-item">Contact</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#" className="nav-item">login</Nav.Link>
            </Nav.Item>
            <Nav.Item>
                <Nav.Link href="#" className="nav-item">register</Nav.Link>
            </Nav.Item>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
