import { Link } from 'react-router-dom';
import { useInView } from 'react-intersection-observer';
import Slider from 'react-slick';

const Menu = () => {
    // const {ref: refHome, inView: inViewHome} = useInView ({
    //     threshold: 0.5,
    //     triggerOnce: true,
    // });

    const sliderSettings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        arrows: false,
        cssEase: "linear",
        afterChange: (index) => setCurrentSlide(index),
      };

  return (
    <div>
        <Slider className="home-slider" {...sliderSettings}>
            <div className="slider-item" style={{backgroundImage: 'url(/assets/bg_3.jpg)'}}>
                <div className="overlay"></div>
                <div className="custom-container">
                    <div className="row slider-text justify-content-center align-items-center">
                        <div className={`col-md-8 col-sm-12 text-center ftco-animate ${
                            currentSlide === index ? "fadeInUp ftco-animated" : ""
                            }`}
                        >
                            <h1 className='mb-3 mt-5 bread'>Our Menu</h1>
                            <p className='breadcrumbs'>
                                <Link to="/">
                                <span className='mr-2'>Home</span>
                                </Link>
                                <span>Menu</span>
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </Slider>
    </div>
  )
}

export default Menu
