import React from "react";
import Slider from "react-slick";
// import "slick-carousel/slick/slick.css";
// import "slick-carousel/slick/slick-theme.css";

// Import Gambar
import bg1 from '../assets/images/bg_1.jpg';
import bg2 from '../assets/images/bg_2.jpg';
import bg3 from '../assets/images/bg_3.jpg';

const Main = () => {
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
  };

  const slides = [
    {
      id: 1,
      background: bg1,
      title: "The Best Coffee Testing Experience",
      description: "A small river named Duden flows by their place and supplies it with the necessary regelialia.",
    },
    {
      id: 2,
      background: bg2,
      title: "Amazing Taste & Beautiful Place",
      description: "A small river named Duden flows by their place and supplies it with the necessary regelialia.",
    },
    {
      id: 3,
      background: bg3,
      title: "Creamy Hot and Ready to Serve",
      description: "A small river named Duden flows by their place and supplies it with the necessary regelialia.",
    },
  ];

  return (
    <section className="home-slider">
      <Slider {...sliderSettings}>
        {slides.map((slide) => (
          <div
            key={slide.id}
            className="slider-item"
            style={{ backgroundImage: `url(${slide.background})` }}
          >
            <div className="overlay"></div>
            <div className="container">
              <div className="row slider-text justify-content-center align-items-center">
                <div className="col-md-8 col-sm-12 text-center">
                  <span className="subheading">Welcome</span>
                  <h1 className="mb-4">{slide.title}</h1>
                  <p className="mb-4 mb-md-5">{slide.description}</p>
                  <p>
                    <a
                      href="#"
                      className="btn btn-primary p-3 px-xl-4 py-xl-3"
                    >
                      Order Now
                    </a>{" "}
                    <a
                      href="#"
                      className="btn btn-white btn-outline-white p-3 px-xl-4 py-xl-3"
                    >
                      View Menu
                    </a>
                  </p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </section>
  );
};

export default Main;
