import React, { useState, useEffect } from 'react';

const Header = () => {
    const [navbarClass, setNavbarClass] = useState("");
  
    useEffect(() => {
      const handleScroll = () => {
        if (window.scrollY > 50) {
          setNavbarClass("scrolled awake");
        } else {
          setNavbarClass("");
        }
      };
  
      window.addEventListener("scroll", handleScroll);
  
      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }, []);

  return (
    <div>
        <div className={`navbar navbar-expand-lg navbar-dark ftco_navbar bg-dark ftco-navbar-light ${navbarClass}`} id="ftco-navbar">
            <div className="container">
                <a className="navbar-brand" href="#">
                    Coffee<small>Blend</small>
                </a>
                <button className='navbar-toggler' type='button' data-toggle='collapse' data-target='#ftco-nav' aria-expanded='false' aria-label='Toggle navigation'>
                    <span className='oi oi-menu'></span> Menu
                </button>
                <div className="collapse navbar-collapse" id="ftco-nav">
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item active"><a href="#" className="nav-link">Home</a></li>
                        <li className="nav-item"><a href="#" className="nav-link">Menu</a></li>
                        <li className="nav-item"><a href="#" className="nav-link">Services</a></li>
                        <li className="nav-item"><a href="#" className="nav-link">About</a></li>
                        <li className="nav-item"><a href="#" className="nav-link">Contact</a></li>

                        {/* login system */}
                        <li className="nav-item"><a href="#" className="nav-link">login</a></li>
                        <li className="nav-item"><a href="#" className="nav-link">register</a></li>
                    </ul>
                </div>
            </div>
        </div>
    </div>
  )
};
export default Header;
