import React, { useEffect } from "react";
import Slider from "react-slick";
import Scrollax from "scrollax";
import image1 from "../assets/images/bg_1.jpg";

const Main = () => {
    useEffect(() => {
        // Inisialisasi Scrollax
        Scrollax.init();
      }, []);
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        waitForAnimate: false
      };
  return (
    <div className="slider-container">
      <Slider className='home-slider' {...settings}>
        <div className="slider-item" style={{backgroundImage: `url(${image1})`}}>
            <div className="overlay"></div>
            <div className="custom-container">
                <div className="row slider-text justify-content-center align-items-center" data-scrollax-parent="true"></div>
            </div>
        </div>
      </Slider>
    </div>
  )
}

export default Main
