import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const slides = [
  { id: 1, background: "/assets/bg_1.jpg", title: "Slide 1" },
  { id: 2, background: "/assets/bg_2.jpg", title: "Slide 2" },
  { id: 3, background: "/assets/bg_3.jpg", title: "Slide 3" },
];

const MySlider = () => {
  const sliderSettings = {
    dots: true,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
  };

  return (
    <Slider {...sliderSettings}>
      {slides.map((slide) => (
        <div
          key={slide.id}
          style={{
            backgroundImage: `url(${slide.background})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            height: "400px",
          }}
        >
          <h2 style={{ color: "#fff", textAlign: "center", paddingTop: "150px" }}>
            {slide.title}
          </h2>
        </div>
      ))}
    </Slider>
  );
};

export default MySlider;
