import React, { useState, useEffect, useRef } from "react";
import Slider from "react-slick";

const Main = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const sliderSettings = {
    dots: true,
    infinite: true,
    fade: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
    cssEase: "linear",
    afterChange: (index) => setCurrentSlide(index), // Update slide aktif
  };

  const slides = [
    {
      id: 1,
      background: "/assets/bg_1.jpg",
      title: "The Best Coffee Testing Experience",
      description: "A small river named Duden flows by their place and supplies it with the necessary regelialia.",
    },
    {
      id: 2,
      background: "/assets/bg_2.jpg",
      title: "Amazing Taste & Beautiful Place",
      description: "A small river named Duden flows by their place and supplies it with the necessary regelialia.",
    },
    {
      id: 3,
      background: "/assets/bg_3.jpg",
      title: "Creamy Hot and Ready to Serve",
      description: "A small river named Duden flows by their place and supplies it with the necessary regelialia.",
    },
  ];

  return (
    <Slider className="home-slider" {...sliderSettings}>
      {slides.map((slide, index) => {
        console.log(slide.background); // Menambahkan console.log di sini
  
        // Kemudian return elemen slide
        return (
          <div
            className={`slider-item ${currentSlide === index ? "active-slide" : ""}`} // Tambahkan kelas berdasarkan slide aktif
            key={slide.id}
            style={{
              backgroundImage: `url('${slide.background}')`,
              backgroundSize: 'cover',
              backgroundPosition: 'center center',
              backgroundRepeat: 'no-repeat',
            }}
          >
            <div className="overlay"></div>
            <div className="custom-container">
              <div className="row slider-text justify-content-center align-items-center">
                <div
                  className={`col-md-8 col-sm-12 text-center ftco-animate ${
                    currentSlide === index ? "fadeInUp ftco-animated" : ""
                  }`}
                >
                  <span className="subheading">Welcome</span>
                  <h1 className="mb-4">{slide.title}</h1>
                  <p className="mb-4 mb-md-5">{slide.description}</p>
                  <p>
                    <a href="#" className="btn btn-primary p-3 px-xl-4 py-xl-3">
                      Order Now
                    </a>{" "}
                    <a
                      href="#"
                      className="btn btn-white btn-outline-white p-3 px-xl-4 py-xl-3"
                    >
                      View Menu
                    </a>
                  </p>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </Slider>
  );
  
};

export default Main;
